try {
	maxPage = 0
	tempUrl = String(result[0]).replace(/\/(\d+)\.html$/, (_match, _$1) => {
		maxPage = _$1
		return '/$page.html'
	})
	result = []
	for (let i = 2; i <= maxPage; i++) {
		result.push(tempUrl.replace(/\$page/, i))
	}
	result
} catch (err) {
	[]
}

