var regex=/第\d*.(\d+)页.当前\d*条.页/;
//正则匹配页数
var page=src.match(regex);
page=page?page[1]:"";
var next=result?result[0]:"";
next=String(next);
var url=[next];
for(i=2;i<=page;i++){
	url.push(next.replace(/_\d+/,"_"+i))
	}
url;

ruleContent.content:
@js:
url="https:"+result.match(/initTxt\("([^"]+)","[^"]+"\)/)[1];
r=String(java.ajax(url).match(/_txt_call\(([\s\S]+\})\)/)[1]);
var r = eval('(' + r + ')');
				if(r.content!=null){				
      var e = r.replace;
				for (var n in e) {
					var i = new RegExp(e[n], "ig");
					r.content = r.content.replace(i, n)
				}
				result=r.content
					}else{result="章节加载失败，或者内容正在手打中，请稍后访问~"}

